import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Table.css";

function Table() {
	const [tasks, setTasks] = useState([]);

	useEffect(() => {
		const fetchTasks = async () => {
			try {
				const res = await axios.get("http://localhost:3001/api/tasks");
				setTasks(res.data);
			} catch (err) {
				console.error("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –æ—Ç—Ä–∏–º–∞–Ω–Ω—ñ –∑–∞–¥–∞—á:", err);
			}
		};

		fetchTasks();
	}, []);

	const toggleComplete = async (taskId) => {
		const taskToUpdate = tasks.find(task => task.id === taskId);
		if (!taskToUpdate) return;

		const newStatus = taskToUpdate.status === 'done' ? 'todo' : 'done';

		try {
			await axios.put(`http://localhost:3001/api/tasks/${taskId}`, { status: newStatus });

			setTasks(prev =>
				prev.map(task =>
					task.id === taskId ? { ...task, status: newStatus } : task
				)
			);
		} catch (err) {
			console.error("–ü–æ–º–∏–ª–∫–∞ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è —Å—Ç–∞—Ç—É—Å—É:", err);
		}
	};

	const handlePriorityChange = async (taskId, newStatus) => {
		try {
			await axios.put(`http://localhost:3001/api/tasks/${taskId}`, { status: newStatus });

			setTasks(prev =>
				prev.map(task =>
					task.id === taskId ? { ...task, status: newStatus } : task
				)
			);
		} catch (err) {
			console.error("–ü–æ–º–∏–ª–∫–∞ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è —Å—Ç–∞—Ç—É—Å—É:", err);
		}
	};

	const formatStatus = (status) => {
		switch (status) {
			case 'todo': return 'todo';
			case 'in_progress': return 'in progress';
			case 'done': return 'done';
			default: return status;
		}
	};

	return (
		<table className="task-table">
			<thead>
				<tr>
					<th>‚úîÔ∏è –ó–∞–≤–µ—Ä—à–µ–Ω–æ</th>
					<th>üìù –ù–∞–∑–≤–∞ –ó–∞–¥–∞—á—ñ</th>
					<th>‚ö° –ü—Ä—ñ–æ—Ä–∏—Ç–µ—Ç</th>
				</tr>
			</thead>
			<tbody>
				{tasks.map(task => (
					<tr
						key={task.id}
						className={`task-row ${task.status === "done" ? "completed" : ""}`}
					>
						<td>
							<input
								type="checkbox"
								checked={task.status === "done"}
								onChange={() => toggleComplete(task.id)}
							/>
						</td>
						<td className="task-name">{task.title}</td>
						<td>
							<select
								className="task-priority"
								value={task.status}
								onChange={(e) => handlePriorityChange(task.id, e.target.value)}
							>
								<option value="todo">{formatStatus('todo')}</option>
								<option value="in_progress">{formatStatus('in_progress')}</option>
								<option value="done">{formatStatus('done')}</option>
							</select>
						</td>
					</tr>
				))}
			</tbody>
		</table>
	);
}

export default Table;
