import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Table.css";

function Table() {
	const [tasks, setTasks] = useState([]);

	useEffect(() => {
		const fetchTasks = async () => {
			try {
				const res = await axios.get("http://localhost:3001/api/tasks");
				setTasks(res.data);
			} catch (err) {
				console.error("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –æ—Ç—Ä–∏–º–∞–Ω–Ω—ñ –∑–∞–¥–∞—á:", err);
			}
		};

		fetchTasks();
	}, []);

	const toggleComplete = (taskId) => {
		// –æ–ø—Ü—ñ–æ–Ω–∞–ª—å–Ω–æ: –æ–Ω–æ–≤–ª–µ–Ω–Ω—è —Å—Ç–∞—Ç—É—Å—É –ø–æ checkbox
		setTasks(prev =>
			prev.map(task =>
				task.id === taskId
					? { ...task, status: task.status === 'done' ? 'todo' : 'done' }
					: task
			)
		);
	};

	const formatStatus = (status) => {
		switch (status) {
			case 'todo': return 'todo';
			case 'in_progress': return 'in progress';
			case 'done': return 'done';
			default: return status;
		}
	};

	return (
		<table className="task-table">
			<thead>
				<tr>
					<th>‚úîÔ∏è –ó–∞–≤–µ—Ä—à–µ–Ω–æ</th>
					<th>üìù –ù–∞–∑–≤–∞ –ó–∞–¥–∞—á—ñ</th>
					<th>‚ö° –ü—Ä—ñ–æ—Ä–∏—Ç–µ—Ç</th>
				</tr>
			</thead>
			<tbody>
				{tasks.map(task => (
					<tr
						key={task.id}
						className={`task-row ${task.status === "done" ? "completed" : ""}`}
					>
						<td>
							<input
								type="checkbox"
								checked={task.status === "done"}
								onChange={() => toggleComplete(task.id)}
							/>
						</td>
						<td className="task-name">{task.title}</td>
						<td>
							<select
								className="task-priority"
								value={task.status}
								onChange={() => { }} // –º–æ–∂–Ω–∞ —Ä–µ–∞–ª—ñ–∑—É–≤–∞—Ç–∏ –∑–º—ñ–Ω—É —Å—Ç–∞—Ç—É—Å—É
							>
								<option value="todo">todo</option>
								<option value="in_progress">in progress</option>
								<option value="done">done</option>
							</select>
						</td>
					</tr>
				))}
			</tbody>
		</table>
	);
}

export default Table;
